import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import Button from '../components/Common/Button';
import Input from '../components/Common/Input';

const Login = () => {
  const [formData, setFormData] = useState({
    phone: '',
    password: '',
    role: 'admin'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const { login, user } = useAuth();
  const navigate = useNavigate();

  // Redirect if already logged in
  useEffect(() => {
    if (user && user.role === 'admin') {
      navigate('/', { replace: true });
    }
  }, [user, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Validate phone number
    const phoneDigits = formData.phone.replace(/\D/g, '');
    if (phoneDigits.length !== 9) {
      setError('Telefon raqam 9 ta raqamdan iborat bo\'lishi kerak');
      setLoading(false);
      return;
    }

    // Prepare login data
    const loginData = {
      ...formData,
      phone: `+998${phoneDigits}`
    };

    try {
      const result = await login(loginData);

      if (result.success) {
        navigate('/', { replace: true });
      } else {
        setError(result.error || 'Kirishda xatolik yuz berdi');
      }
    } catch (err) {
      setError('Tarmoq xatoligi yuz berdi');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Format phone number input
    if (name === 'phone') {
      const digits = value.replace(/\D/g, '');
      if (digits.length <= 9) {
        setFormData(prev => ({
          ...prev,
          [name]: digits
        }));
      }
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        {/* Logo */}
        <div className="login-logo">
          üè´
        </div>

        {/* Title */}
        <h1 className="login-title">Admin Paneli</h1>
        <p className="text-center text-gray-600 mb-6">
          Maktab boshqaruv tizimiga xush kelibsiz
        </p>

        {/* Form */}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="form-group">
            <label className="form-label">Telefon raqam</label>
            <div className="flex items-center">
              <span className="form-input-prefix">+998</span>
              <Input
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="901234567"
                pattern="[0-9]{9}"
                maxLength="9"
                required
              />
            </div>
            <small className="text-gray-500 text-xs mt-1">
              9 ta raqam kiriting (90, 91, 93, 94, 95, 97, 98, 99)
            </small>
          </div>

          <div className="form-group">
            <label className="form-label">Parol</label>
            <Input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Parolni kiriting"
              required
              minLength="3"
            />
          </div>

          <div className="form-group">
            <label className="form-label">Rol</label>
            <select
              name="role"
              value={formData.role}
              onChange={handleChange}
              className="form-select"
              required
            >
              <option value="admin">Administrator</option>
            </select>
            <small className="text-gray-500 text-xs mt-1">
              Faqat administrator paneliga kirish
            </small>
          </div>

          {error && (
            <div className="error">
              {error}
            </div>
          )}

          <Button
            type="submit"
            variant="primary"
            className="w-full"
            loading={loading}
            disabled={loading}
          >
            {loading ? 'Kirilmoqda...' : 'Kirish'}
          </Button>
        </form>

        {/* Demo credentials */}
        <div className="mt-6 p-3 bg-gray-50 rounded-lg">
          <h4 className="text-sm font-medium text-gray-700 mb-2">Demo ma'lumotlar:</h4>
          <div className="text-xs text-gray-600 space-y-1">
            <div>üì± Telefon: 990330919</div>
            <div>üîë Parol: admin123</div>
          </div>
        </div>

        {/* Footer */}
        <div className="text-center mt-6">
          <p className="text-xs text-gray-500">
            ¬© 2025 Maktab Boshqaruv Tizimi v2.0.0
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
